def get(not_stop_words=('the'), as_set=True):
    '''(iterable) -> list
    get list of all stopwords

    non_stop_words: remove these from the returned list

    Example:
    >>>stopwords.get()
    ['a', 'above', ...]

    >>>stopwords.get(('a, able'))
    ['about', 'above', ...]
    '''
